import React, { useEffect, useState,useRef } from 'react'
import AddInput from '../AddInput/AddInput'
import Header from '../Header/Header'
import TodoList from '../TodoList/TodoList'
import "./Todo.css"

function Todo() {

    const [todos, setTodos] = useState([])
    const [change,setChange] = useState(0);
    const numberOfTasks = useRef(0);

    useEffect(() => {
        numberOfTasks.current= numberOfTasks.current+change;
      });

    return (
        <div className="todo">
            <Header title="Todo" />
            <AddInput 
                setTodos={setTodos}
                todos={todos}
                setChange={setChange}
            />
            <TodoList 
                todos={todos}
                setTodos={setTodos}
                setChange={setChange}
            />
        </div>
    )
}

export default Todo
